mapper.update:
update community_board
set
    cb_title = #{cb_title},
    cb_content = #{cb_content},
    cb_file = #{cb_file}
where cb_no = #{cb_no}

mapper.select:
select cb.cb_no, cb.board_no, cb.cb_title, cb.cb_content, cb.cb_file, u.user_name, u.user_num, cb.categoryNum, t.state, t.district, cb.cb_currentview, cb.cb_addr, cb.cb_view, cb.cb_like, cb_regdate from community_board cb join user u on cb.user_num = u.user_num join territory t on cb.cb_addr = t.no WHERE 1 = 1
AND board_no = #{board_no}
AND CategoryNum = #{categoryNum}
AND cb_addr = #{cb_addr}
order by cb_no, cb_regdate desc

select count(1) > 0
from likeCount
where cb_no = #{cb_no} and user_num = #{user_num}

mapper.update:
update community_board
set cb_like = cb_like +1
where cb_no = #{cb_no}

update community_board
set cb_like = cb_like -1
where cb_no = #{cb_no}

mapper.select:
select cb_like
from community_board
where cb_no = #{cb_no}

mapper.update:
update community_board
set cb_view = cb_view+1
where cb_no = #{cb_no}
